._slug {
	display: flex;
	flex-flow: row wrap;
	align-items: flex-start;
	padding: 10px;
	--text-color: #000;
	--background: #fff;
    --box-shadow: 0 0 8px 0 #0003;
    --border-radius: 10px;
    --link-color: #006699;
    --link-hover-color: #0099cc;
}
._slug-post {
    width: 300px;
    margin: 20px;
    display: flex;
    flex-flow: column nowrap;
    gap: 10px;
