    --accent-bg: #006699;
    --accent-color: #fff;
    --box-shadow: 0 0 8px 0 #0003;
}
._slug-post {
    position: relative;
    width: 300px;
    aspect-ratio: 4 / 3;
    box-shadow: var(--box-shadow);
}
._slug-post.no-img { display: none; } /* hides posts without featured image */
._slug-open {
    position: absolute;
    display: block;
    width: 100%;
    height: 100%;
}
._slug-img {
	position: absolute;
	inset: 0;
	width: 100%;
	height: 100%;
	background-size: cover;
    background-position: center;
}
._slug a { color: var(--link-color) !important; }
._slug a:hover { color: var(--link-hover-color) !important; }
._slug-text-wrapper {
    position: absolute;
    display: flex;
    flex-flow: column nowrap;
    justify-content: center;
    align-items: center;
    gap: 10px;
    padding: 5px;
    width: 100%;
    height: 100%;
    background-color: var(--bg-mask);
    color: var(--accent-color);
    opacity: 0;
    transition: opacity 300ms ease;
}
._slug-post:hover ._slug-text-wrapper { opacity: 1; }
._slug-row { display: flex; gap: 5px; }

._slug-post:hover ._slug-bar-shadow { left: -10px; }
._slug-title { font-weight: bold; }
._slug-bar-shadow {
    filter: drop-shadow(-1px 5px 3px #0009);
    position: absolute;
    z-index: 1;
    left: -20px;
    top: 20px;
    transition: left 300ms ease;
}
._slug-bar-wrapper {
    width: max-content;
    height: 36px;
    background-color: var(--accent-bg);
    color: var(--accent-color);
    padding: 5px 10px;
    clip-path: polygon(0 0, 95% 0, 100% 50%, 95% 100%, 0 100%, 5% 50%);
}
._slug-terms { display: flex; gap: 10px; }

