}

._slug .carousel-counter {
    display: flex;
	justify-content: center;
	width: 100%;
}

._slug {
    --text-color: #000;
    --background: #fff;
    --anim-speed: 300ms;
    --shadow: 2px 2px 4px 0 #0003;
    color: #000;
    display: flex;
    flex-flow: row wrap;
    gap: 20px;
    justify-content: center;
    padding: 20px;
}
._slug-post {
    position: relative;
    width: 360px;
    height: 420px;
    overflow: hidden;
    box-shadow: var(--shadow);
    color: var(--text-color);
}
._slug-img {
    position: absolute;
    inset: 0;
    background-size: cover;
    background-position: center;
}
._slug-main-flex {
    position: absolute;
    inset: 0;
    display: flex;
    flex-flow: column nowrap;
}
._slug-dummy {
    flex-grow: 1;
    transition: all 300ms ease-out;
}
._slug-post:hover ._slug-dummy, .no-img ._slug-dummy { flex-grow: 0; }
._slug-info {
    flex-grow: 0;
    width: 100%;
    max-height: max-content;
    background: var(--background);
    box-shadow: var(--shadow);
    transition: all var(--anim-speed) ease-out;
}
._slug-post:hover ._slug-info, .no-img ._slug-info { flex-grow: 1; }
._slug-info-above { padding: 10px 5px; }
._slug-title { margin: 0; text-align: center; }
._slug-short-excerpt {
    overflow: hidden;
    max-height: 2.4em;
    line-height: 1.2em;
    transition: max-height var(--anim-speed) ease-out;
}
._slug-post:hover ._slug-short-excerpt { max-height: 0; }
.no-img ._slug-short-excerpt { display: none; }
._slug-info-below {
    position: absolute;
    padding: 10px;
    width: 100%;
    display: flex;
    flex-flow: column nowrap;
    gap: 5px;
}
._slug-excerpt {
    overflow: hidden;
    text-overflow: clip;
}
._slug-row { display: flex; gap: 5px; }
._slug-space { justify-content: space-between; }
